import React, { Component } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";

import { Title, Content, Card, SendButton, MyInput } from "./styles";
import api from "../../services/apiCards";

import { TextField } from '@material-ui/core';
import DeleteIcon from "@material-ui/icons/Delete";
import CardItem from '../../components/CardItem';
import * as personActions from "../../store/modules/person/actions";

import IconButton from "@material-ui/core/IconButton";
import Button from '@material-ui/core/Button';
import PostAddIcon from '@material-ui/icons/PostAdd';
import Paper from '@material-ui/core/Paper';
import LogoutIcon from '@material-ui/icons/ExitToApp';


class Main extends Component{
  state = {
    cards: [{}],
    title: '',
    content: '',
  };

  async componentDidMount() {
    const { user } = this.props;
    if (user.length > 0 ) {
      const response = await api.get("/cards", {
      headers: { Authorization: `Bearer ${user[0].token}`},
    });

    this.setState({ cards: response.data });

    console.log(response.data);
    }
  };

  componentDidUpdate(prevProps, prevState) {};//executa quando altera o state local

  handleInputChange = event => {
    const { target } = event;

    if (target.name === 'title') {
      this.setState({ title: target.value });
    } else {
      this.setState({ content: target.value });
    }
  };

  handleLogout = event => {
    localStorage.removeItem('persist:react-cards');
    window.location.reload();
  }


  handleSubmit = async event => {
    event.preventDefault();

    const { user } = this.props;

    const response = await api.post(
      '/cards',
      {
        title: this.state.title,
        content: this.state.content,
      },
      { headers: { Authorization: `Bearer ${user[0].token}` } }
    );
    this.setState({
      cards: [
        ...this.state.cards,
        {
          id: response.data.id,
          title: this.state.title,
          content: this.state.content,
        },
      ],
    });
  };

  sendToStore = (person) => {
    const { addToPerson } = this.props;
    addToPerson(person);
  }

  removeToStore = person => {
    const { removeFromPerson } = this.props;

    removeFromPerson(person);
  }

  handleDelete = async id => {
    const {user} = this.props;
    const response = await api.delete("/cards/${id}",{
        headers: { Authorization: `Bearer ${user[0].token}`},
      });

    this.setState({
      cards: this.state.cards.filter(item => item.id !== id),
    });
  };

  render() {
    return (
      <>
        <Card>
          <ul>
              <Button onClick={this.handleLogout} startIcon={<LogoutIcon />}></Button>

              <Title>Recados</Title>
              <form onSubmit={this.handleSubmit}>
                    <MyInput
                      label="Digite um titulo:"
                      type="text"
                      name="title"
                      onChange={this.handleInputChange}
                      defaultValue={this.state.newCard.title}
                    />
                    <MyInput
                      label="Digite um conteudo:"
                      type="text"
                      name="content"
                      onChange={this.handleInputChange}
                      defaultValue={this.state.newCard.content}
                    />
                    <SendButton type="submit">Enviar</SendButton>
              </form>
          </ul>


          <ul>
             {this.state.cards.map(item => (
                <Paper>
                  <CardItem
                    key={item.id}
                    card={item}
                    onDelete={() => this.handleDelete(item.id)}
                  />
                </Paper>
              ))}
          </ul>
      </Card>
    </>
  );
  }
}

const mapStateToProps = state => ({
  user: state.user,
});

const mapDispatchToProps = dispatch =>
bindActionCreators(personActions, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(Main);
