import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";

import React, { Component } from 'react';

import { Title, Card, SendButton, MyInput } from './styles';

class Login extends Component {
  state = {
    login: '',
    password: '',
  };

  componentDidMount() {
    const token = localStorage.getItem('token');
    if (token) {
      this.setState({ login: true });
    }
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevProps !== this.state.login) {
      localStorage.setItem('token', this.state.token);
    }
  }

  handleSubmit = value => e => {
    e.preventDefault();
    localStorage.setItem('token', value);
    this.setState({
      token: [...this.state.token, this.state.token],
    });
  };

  render() {
    return (
      <>
        <Card>
          <center>
            <ul>
              <Title>Login</Title>
              <form onSubmit={this.handleSubmit('abc')}>
                <MyInput label="Digite seu email:" name="email" type="text" />
                <MyInput
                  label="Digite sua senha:"
                  name="password"
                  type="password"
                />
                <SendButton type="submit">Enviar</SendButton>
              </form>
            </ul>
          </center>
        </Card>
      </>
    );
  }
}
export default Login;
