export function addToPerson(person) {
  return {
    type: "@person/ADD",
    person,
  };
}

export function showAlertTotal() {
  return {
    type: "@person/SHOW_PERSON",
  };
}

export function removeFromPerson(person) {
  return {
    type: "@person/REMOVE",
    person,
  };
}
