import React from "react";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import Container from "@material-ui/core/Container";

class Main extends Component{
  state = {
    login: "",
    password: [],
  };

  async componentDidMount() {
    const { person } = this.props;
    const response = await api.get("/cards");

    console.log(response.data);

    if(person.length > 0) {
      this.setState({ persons: person });
    }
  }

  componentDidUpdate(prevProps, prevState) {}//executa quando altera o state local

  handleInputChange = e => {
    this.setState({newPerson: e.target.value});
  }

  handleSubmit = e => {
    e.preventDefault();

    this.sendToStore(this.state.newPerson);

    this.setState({persons: [...this.state.persons, this.state.newPerson],
      newPerson: '',
    });
  }

  sendToStore = (person) => {
    const { addToPerson } = this.props;
    addToPerson(person);
  }

  removeToStore = person => {
    const { removeFromPerson } = this.props;

    removeFromPerson(person);
  }

  handleDelete = person => {
    this.setState({
      persons: this.state.persons.filter(item => item !== person),
    });

    this.removeToStore(person);
  };

  render() {
    return (
      <>
      <Card>
      <Title>Lista de Aniversariantes do mÃªs <CakeIcon color="secundary" style={{ fontSize: 50 }}/></Title>
        <Cont>
        <ul>
          {
            this.state.persons.map(item =>(
            <PersonItem
              key = {item}
              person = {item}
              onDelete = {() => this.handleDelete(item)}
            />
            ))}
        </ul>
        <br/>
        <form onSubmit={this.handleSubmit}>
        <TextField
          label="Digite um nome"
          type="text"
          onChange={this.handleInputChange}
          value={this.state.newPerson}
        />
        <IconButton onClick={this.handleSubmit} aria-label="salvar">
          <PersonAddIcon type="button" style={{ fontSize: 40 }}/>
      </IconButton>
        </form>
        </Cont>

      </Card>
      </>
    );
  }
}

export default Login;
